public class AmazonTest {


    public static String getNextSpecialString(String s){
        char[] arr = s.toCharArray();
        // get pointer to first repeated char or last char
        int i = 1;
        while( i < arr.length) {
            if(arr[i] == arr[i-1]){break;}
            i++;
        }
        if(i == arr.length){ i--; }
        while(i >=0){
            if(arr[i] !='z'){
                char temp = arr[i];
                for(char c = (char)(arr[i] +1); c <= 'z'; c++){
                    arr[i] = c;
                    if(i == 0 ||  arr[i] != arr[i-1]){
                        fillTillLast(arr, i+1);
                        return String.valueOf(arr);
                    }
                }
                arr[i] = temp;
            }
            i--;
        }
        return "-1";
    }

    private static void fillTillLast(char[] arr, int start){
        char[] ab = new char[]{'a', 'b'};
        int crr = 0;
        while(start < arr.length){
            arr[start] = ab[crr];
            crr = (crr + 1) % ab.length;
            start ++;
        }
    }


    public static void main(String[] args) {
        System.out.println(getNextSpecialString("abbc"));       // ✅ abca
        System.out.println(getNextSpecialString("abcdde"));     // ✅ abcdab
        System.out.println(getNextSpecialString("zzab"));       // ✅ -1
        System.out.println(getNextSpecialString("abababab"));   // ✅ abababac
        System.out.println(getNextSpecialString("zz"));         // ✅ -1
        System.out.println(getNextSpecialString("abbd"));       // ✅ abca
        System.out.println(getNextSpecialString("jiaab"));
        System.out.println(getNextSpecialString(""));        // ✅ aza
        System.out.println((getNextSpecialString("")));
    }
}
